02118     // Token: 0x06001276 RID: 4726 RVA: 0x000791D4 File Offset: 0x000773D4
02119     public bool IsExcludeFromCraft()
02120     {
02121         if (this.IsUnique || this.c_isImportant)
02122         {
02123             return true;
02124         }
02125         Card card = this.parent as Card;
02126         if (card != null)
02127         {
02128             if (card.trait is TraitChestMerchant)
02129             {
02130                 return true;
02131             }
02132             if (card.isSale)
02133             {
02134                 return true;
02135             }
02136             Window.SaveData windowSaveData = card.GetWindowSaveData();
02137             if (windowSaveData != null)
02138             {
02139                 return windowSaveData.excludeCraft;
02140             }
02141         }
02142         return false;
02143     }
