02248     // Token: 0x17000530 RID: 1328
02249     // (get) Token: 0x06001282 RID: 4738 RVA: 0x00079318 File Offset: 0x00077518
02250     public int ChildrenWeight
02251     {
02252         get
02253         {
02254             if (this.dirtyWeight)
02255             {
02256                 this._childrenWeight = 0;
02257                 if (!(this.trait is TraitMagicChest))
02258                 {
02259                     foreach (Thing thing in this.things)
02260                     {
02261                         this._childrenWeight += thing.ChildrenAndSelfWeight;
02262                     }
02263                     this.dirtyWeight = false;
02264                     Card card = this.parent as Card;
02265                     if (card != null)
02266                     {
02267                         card.SetDirtyWeight();
02268                     }
02269                     if (this.isChara && this.IsPCFaction)
02270                     {
02271                         this.Chara.CalcBurden();
02272                     }
02273                     if (this._childrenWeight < 0 || this._childrenWeight >= 10000000)
02274                     {
02275                         this._childrenWeight = 9999999;
02276                     }
02277                 }
02278             }
02279             return this._childrenWeight * Mathf.Max(100 - this.Evalue(404), 0) / 100;
02280         }
02281     }
