03198     // Token: 0x060012DF RID: 4831 RVA: 0x00079A88 File Offset: 0x00077C88
03199     public int GetBestAttribute()
03200     {
03201         int num = 1;
03202         foreach (Element element in from a in this.elements.dict.Values
03203         where Element.List_MainAttributesMajor.Contains(a.id)
03204         select a)
03205         {
03206             int num2 = element.Value;
03207             if (this.isChara && this.Chara.tempElements != null)
03208             {
03209                 num2 -= this.Chara.tempElements.Value(element.id);
03210             }
03211             if (num2 > num)
03212             {
03213                 num = num2;
03214             }
03215         }
03216         return num;
03217     }
