01600     // Token: 0x0600074E RID: 1870 RVA: 0x00030168 File Offset: 0x0002E368
01601     public List<Card> GetListGuide()
01602     {
01603         List<Card> list = new List<Card>();
01604         foreach (Thing thing in EClass._map.things)
01605         {
01606             if (thing.IsInstalled && thing.trait.GuidePriotiy > 0)
01607             {
01608                 list.Add(thing);
01609             }
01610         }
01611         foreach (Chara chara in EClass._map.charas)
01612         {
01613             if (!chara.IsPCParty && chara.trait.GuidePriotiy > 0)
01614             {
01615                 list.Add(chara);
01616             }
01617         }
01618         list.Sort((Card a, Card b) => b.trait.GuidePriotiy - a.trait.GuidePriotiy);
01619         return list;
01620     }
