01295     // Token: 0x0600052B RID: 1323 RVA: 0x00024CC0 File Offset: 0x00022EC0
01296     public void ModKarma(int a)
01297     {
01298         if (a == 0)
01299         {
01300             return;
01301         }
01302         if (a < 0)
01303         {
01304             Tutorial.Reserve("karma", null);
01305         }
01306         bool flag = this.karma < 0;
01307         this.karma += a;
01308         Msg.Say((a > 0) ? "karmaUp" : "karmaDown", a.ToString() ?? "", null, null, null);
01309         if (this.karma < 0 && !flag)
01310         {
01311             Msg.Say("becomeCriminal");
01312             EClass.pc.pos.TryWitnessCrime(EClass.pc, null, 4, null);
01313             EClass._zone.RefreshCriminal();
01314             Tutorial.Reserve("criminal", null);
01315         }
01316         if (this.karma >= 0 && flag)
01317         {
01318             Msg.Say("becomeNonCriminal");
01319             EClass._zone.RefreshCriminal();
01320         }
01321         EClass.game.quests.list.ForeachReverse(delegate(Quest q)
01322         {
01323             q.OnModKarma(a);
01324         });
01325         this.karma = Mathf.Clamp(this.karma, -100, 100);
01326     }
