01243     public virtual LockOpenState TryOpenLock(Chara cc, bool msgFail = true)
01244     {
01245         Thing thing = cc.things.Find<TraitLockpick>();
01246         int num = (thing == null) ? (cc.Evalue(280) / 2 + 2) : (cc.Evalue(280) + 10);
01247         int num2 = this.owner.c_lockLv;
01248         bool flag = this is TraitChestPractice;
01249         if (flag)
01250         {
01251             num2 = num / 4 * 3 - 1;
01252         }
01253         if (num <= num2 && cc.IsPC)
01254         {
01255             cc.PlaySound("lock", 1f, true);
01256             cc.Say("openLockFail2", null, null);
01257             this.owner.PlayAnime(AnimeID.Shiver, false);
01258             return LockOpenState.NotEnoughSkill;
01259         }
01260         if (thing != null && !flag)
01261         {
01262             thing.ModCharge(-1, true);
01263         }
01264         if (EClass.rnd(num + 6) > num2 + 5 || (!cc.IsPC && EClass.rnd(20) == 0) || EClass.rnd(200) == 0)
01265         {
01266             cc.PlaySound("lock_open", 1f, true);
01267             cc.Say("lockpick_success", cc, this.owner, null, null);
01268             int num3 = 100 + num2 * 10;
01269             if (this.owner.c_lockedHard)
01270             {
01271                 num3 *= 10;
01272             }
01273             cc.ModExp(280, num3);
01274             this.owner.c_lockLv = 0;
01275             if (this.owner.c_lockedHard)
01276             {
01277                 this.owner.c_lockedHard = false;
01278                 this.owner.c_priceAdd = 0;
01279             }
01280             if (cc.IsPC && this.owner.isLostProperty)
01281             {
01282                 EClass.player.ModKarma(-8);
01283             }
01284             this.owner.isLostProperty = false;
01285             return LockOpenState.Success;
01286         }
01287         cc.PlaySound("lock", 1f, true);
01288         if (cc.IsPC)
01289         {
01290             cc.Say("openLockFail", null, null);
01291         }
01292         cc.ModExp(280, (thing != null) ? 50 : 30);
01293         if (thing == null | EClass.rnd(2) == 0)
01294         {
01295             cc.stamina.Mod(-1);
01296         }
01297         return LockOpenState.Fail;
