01870                 {
01871                     p.TrySetAct(flag ? "ActTorch" : "ActToggleOn", delegate()
01872                     {
01873                         this.Toggle(true, false);
01874                         return true;
01875                     }, this.owner, null, 1, false, true, false);
01876                 }
