01893                 }, this.owner, null, 1, false, true, false);
01894             }
01895             return;
01896         }
01897         case ToggleType.Curtain:
01898             p.TrySetAct(this.owner.isOn ? "actOpen" : "actClose", delegate()
01899             {
01900                 this.Toggle(!this.owner.isOn, false);
01901                 return true;
