01916     // Token: 0x06001928 RID: 6440 RVA: 0x0009FC68 File Offset: 0x0009DE68
01917     public bool IsFuelEnough(int num = 1, List<Thing> excludes = null, bool tryRefuel = true)
01918     {
01919         if (!this.IsRequireFuel)
01920         {
01921             return true;
01922         }
01923         if (this.owner.c_charges >= this.FuelCost * num)
