00058             if (row.playable == 1 || (row.playable <= 6 && this.extraRace) || (flag && row.playable != 9))
00059             {
00060                 this.races.Add(row);
00061             }
00062         }
00063         foreach (SourceJob.Row row2 in EMono.sources.jobs.rows)
00064         {
00065             if (row2.playable == 1 || (row2.playable <= 6 && this.extraRace) || (flag && row2.playable != 9))
00066             {
00067                 this.jobs.Add(row2);
00068             }
00069         }
00070         this.races.Sort((SourceRace.Row a, SourceRace.Row b) => (a.playable - b.playable) * 10000 + a._index - b._index);
00071         this.jobs.Sort((SourceJob.Row a, SourceJob.Row b) => (a.playable - b.playable) * 10000 + a._index - b._index);
